DROP PROCEDURE IF EXISTS `generate_users`;
delimiter ;;
CREATE DEFINER=`root`@`%` PROCEDURE `generate_users`()
BEGIN
    DECLARE i INT DEFAULT 1;
    WHILE i <= 500 DO
        VALUES (
            CONCAT('auto_user_', i), 
            'e10adc3949ba59abbe56e057f20f883e', -- 默认密码 123456
            CONCAT('书友', FLOOR(RAND() * 100000)), 
            0,
            NOW()
        );
        SET i = i + 1;
    END WHILE;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for NewProc
-- ----------------------------
DROP PROCEDURE IF EXISTS `NewProc`;
delimiter ;;
CREATE DEFINER=`root`@`%` PROCEDURE `NewProc`()
BEGIN
    DECLARE done INT DEFAULT 0;
    DECLARE u_id BIGINT;
    DECLARE b_id BIGINT;
    DECLARE b_cat INT;
    DECLARE rating_score DECIMAL(2,1);
    DECLARE rating_comment VARCHAR(100);
    
    -- 游标：遍历所有自动生成的普通用户 (ID > 60 是我们刚生成的)
    DECLARE cur_user CURSOR FOR SELECT id FROM sys_user WHERE username LIKE 'auto_user_%';
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;

    OPEN cur_user;

    read_loop: LOOP
        FETCH cur_user INTO u_id;
        IF done THEN
            LEAVE read_loop;
        END IF;

        -- ---------------------------------------------------------
        -- 模拟逻辑：每个用户随机评价 10 到 20 本书
        -- ---------------------------------------------------------
        SET @num_books_to_rate = FLOOR(10 + (RAND() * 10)); 
        SET @count = 0;

        rating_loop: LOOP
            IF @count >= @num_books_to_rate THEN
                LEAVE rating_loop;
            END IF;

            -- 随机选一本书
            SELECT id, category_id INTO b_id, b_cat FROM book_info ORDER BY RAND() LIMIT 1;
            
            -- 判断用户属性 (偶数ID=理工男, 奇数ID=文艺青年)
            IF (u_id % 2 = 0) THEN
                -- === 理工男逻辑 ===
                IF b_cat IN (1, 2) THEN 
                    -- 遇到科幻或编程：80%概率打高分(4.0-5.0)，20%概率打中等分
                     SET rating_score = 4.0 + (RAND() * 1.0); 
                     SET rating_comment = ELT(FLOOR(1 + (RAND() * 4)), '神作', '技术含量高', '逻辑严密', '强推');
                ELSE
                    -- 遇到文学或悬疑：大概率不打分(直接跳过)，小概率打低分(2.0-3.5)
                    IF (RAND() > 0.3) THEN
                        ITERATE rating_loop; -- 跳过，不评分
                    END IF;
                    SET rating_score = 2.0 + (RAND() * 1.5);
                    SET rating_comment = ELT(FLOOR(1 + (RAND() * 3)), '看不懂', '太矫情', '一般般');
                END IF;
            ELSE
                -- === 文艺青年逻辑 ===
                IF b_cat IN (3, 4) THEN
                    -- 遇到文学或悬疑：高分
                    SET rating_score = 4.0 + (RAND() * 1.0);
                    SET rating_comment = ELT(FLOOR(1 + (RAND() * 4)), '文笔优美', '剧情反转', '感人至深', '太好看了');
                ELSE
                    -- 遇到编程：低分或跳过
                    IF (RAND() > 0.3) THEN
                        ITERATE rating_loop;
                    END IF;
                    SET rating_score = 1.0 + (RAND() * 2.0); -- 1.0 - 3.0
                    SET rating_comment = '太难了看不懂';
                END IF;
            END IF;

            -- 插入评分 (使用 INSERT IGNORE 防止重复对同一本书评分报错)
            VALUES (u_id, b_id, rating_score, rating_comment, NOW());
            
            -- 顺便插入一点浏览记录，丰富数据
            VALUES (u_id, b_id, NOW());

            SET @count = @count + 1;
        END LOOP rating_loop;

    END LOOP;

    CLOSE cur_user;
END
;;
delimiter ;

